(()=>{const e=document.querySelector(".sign-up-container form");e.addEventListener("submit",(t=>{t.preventDefault(),e.querySelector('input[type="text"]').value;const n=e.querySelector('input[type="email"]').value,s=e.querySelector('input[type="password"]').value;firebase.auth().createUserWithEmailAndPassword(n,s).then((e=>{const t=e.user;console.log(t)})).catch((e=>{e.code;const t=e.message;console.log(t)}))}));const t=document.querySelector(".sign-in-container form");t.addEventListener("submit",(e=>{e.preventDefault();const n=t.querySelector('input[type="email"]').value,s=t.querySelector('input[type="password"]').value;firebase.auth().signInWithEmailAndPassword(n,s).then((e=>{const t=e.user;console.log(t)})).catch((e=>{e.code;const t=e.message;console.log(t)}))}));const n=document.getElementById("signUp"),s=document.getElementById("signIn"),o=document.getElementById("container");n.addEventListener("click",(()=>{o.classList.add("right-panel-active")})),s.addEventListener("click",(()=>{o.classList.remove("right-panel-active")}))})();